$.components.register("input-group-file",{api:function(){$(document).on("change",".input-group-file [type=file]",function(){var n=$(this),i=$(this).parents(".input-group-file").find(".form-control"),t="";$.each(n[0].files,function(n,i){t+=i.name+", "}),t=t.substring(0,t.length-2),i.val(t)})}});